import React from 'react';
import './App.css';
import Keys from './components/Keyboard';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calcInput: ""
    }
    this.display = ""
  }

  handleInput = (e) => {
    let input = this.state.calcInput;
    input += e.target.value;
    this.display = input;
  }

  display = () => {
    this.setState({
      calcInput: this.display
    });
  }

  clear = () => {
    this.setState({
      calcInput: ""
    });
  }

  calculate = () => {
    let str = String(this.state.display);
    let result = eval(str);
    this.setState({
      cal: result
    });
  }

  render() {
    return (
      <div className="App container mt-5">
        <input type="text" value={this.display} className="w-100" readOnly />
        <Keys handleInput={this.handleInput} clear={this.clear} />
      </div>
    );
  }
}

export default App;
