import React from 'react';
import './App.css';
import Keyboard from './components/Keyboard';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      calcInput: ""
    }
  }

  output = (e) => {
    this.setState({
      calcInput: e.target.value
    })
  }

  render() {
    return (
      <div className="App container mt-5">
        <input type="text" value={this.state.calcInput} className="w-100" readOnly />
        <Keyboard output={this.output} />
      </div>
    );
  }
}

export default App;
